import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  SafeAreaView,
  Dimensions,
  TouchableOpacity,
  ScrollView,
  Modal,
  Button,
} from "react-native";
import { SimpleLineIcons } from "@expo/vector-icons";
import Colors from "../Constants/Colors";

let screenWidth = Dimensions.get("window").width;
const ZonesScreen = (props) => {
  const [show, setShow] = useState(false);
  const [isPresed, setIsPressed] = useState(false);

  const radioHandler = () => {
    setIsPressed(!isPresed);
  };

  const modalToggel = () => {
    setShow(!show);
  };
  return (
    <SafeAreaView>
      <ScrollView>
        <View style={styles.header}></View>
        <View style={styles.container}>
          <Text style={styles.boldText}>Zone {props.route.params.id}</Text>
          <Text style={styles.mediumText}>Type de produit</Text>
          <View style={{ marginVertical: 20 }}>
            <Text style={styles.smallText}>Contrler l'irrigation</Text>
            {props.route.params.etat ? (
              <View style={styles.monitor}>
                <TouchableOpacity disabled>
                  <SimpleLineIcons
                    name="control-play"
                    size={screenWidth * 0.12}
                    color={Colors.grey_fade}
                  />
                </TouchableOpacity>
                <TouchableOpacity>
                  <SimpleLineIcons
                    name="control-pause"
                    size={screenWidth * 0.12}
                    color={Colors.dark_grey}
                  />
                </TouchableOpacity>
                <TouchableOpacity onPress={modalToggel}>
                  <SimpleLineIcons
                    name="info"
                    size={screenWidth * 0.12}
                    color={Colors.dark_grey}
                  />
                </TouchableOpacity>
              </View>
            ) : (
              <View style={styles.monitor}>
                <TouchableOpacity>
                  <SimpleLineIcons
                    name="control-play"
                    size={screenWidth * 0.12}
                    color={Colors.dark_grey}
                  />
                </TouchableOpacity>
                <TouchableOpacity disabled>
                  <SimpleLineIcons
                    name="control-pause"
                    size={screenWidth * 0.12}
                    color={Colors.grey_fade}
                  />
                </TouchableOpacity>
                <TouchableOpacity onPress={modalToggel}>
                  <SimpleLineIcons
                    name="info"
                    size={screenWidth * 0.12}
                    color={Colors.dark_grey}
                  />
                </TouchableOpacity>
              </View>
            )}
          </View>
          <View>
            <Text style={styles.boldText}>Programme d'Irrigation</Text>
            <View style={{ marginTop: 30, marginBottom: 20 }}>
              <View style={styles.days}>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Dim.</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Lun.</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Mar.</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Mer.</Text>
                </TouchableOpacity>
              </View>
              <View
                style={{
                  ...styles.days,
                  ...{ width: screenWidth * 0.6, alignSelf: "center" },
                }}
              >
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Jeu.</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Ven.</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.day}>
                  <Text style={styles.dayText}>Sam.</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
          <View
            style={{
              marginHorizontal: 30,
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Text style={styles.boldText}>Irrigation intéligente</Text>
            <Text style={styles.description}>
              Le systéme vas manager Votre irrigation celon une étude bien
              précise. Cliquez sur le bouton ci-dessous pour activer cette
              option
            </Text>
            <TouchableOpacity
              onPress={radioHandler}
              style={{
                ...styles.radio,
                ...{ alignItems: isPresed ? "flex-end" : "flex-start" },
              }}
            >
              <View
                style={{
                  ...styles.but,
                  ...{
                    backgroundColor: !isPresed ? Colors.silver : Colors.success,
                  },
                }}
              ></View>
            </TouchableOpacity>
          </View>
          <View style={{ flexDirection: "row", marginTop: 20 }}>
            <Text style={styles.smallText}>Prochaine irrigation dans: </Text>
            <Text style={{ ...styles.smallText, ...{ color: Colors.danger } }}>
              2 Jours 15 h 35 Min
            </Text>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  header: {
    height: 90,
    alignItems: "center",
    justifyContent: "space-evenly",
  },
  container: {
    elevation: 20,
    justifyContent: "flex-start",
    alignItems: "center",
    backgroundColor: "white",
    paddingVertical: 20,
    borderTopLeftRadius: 40,
    borderTopRightRadius: 40,
    height: "100%",
  },
  
  boldText: {
    fontFamily: "futura-meduim",
    fontSize: screenWidth * 0.065,
    fontWeight: "bold",
    alignSelf: "center",
  },
  mediumText: {
    fontSize: screenWidth * 0.055,
    color: "black",
  },
  smallText: {
    fontFamily: "futura-meduim",
    fontSize: screenWidth * 0.035,
    alignSelf: "center",
    color: Colors.dark_grey,
  },
  dayText: {
    fontSize: screenWidth * 0.05,
    fontWeight: "bold",
    color: Colors.dark_grey,
  },

  description: {
    fontFamily: "futura-meduim",
    fontSize: screenWidth * 0.04,
    textAlign: "center",
    marginVertical: 15,
    color: Colors.dark_grey,
  },
  monitor: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 15,
    width: screenWidth * 0.4,
  },
  days: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-evenly",
  },
  day: {
    alignItems: "center",
    justifyContent: "center",
    borderWidth: 1,
    borderColor: Colors.silver,
    width: screenWidth * 0.14,
    height: screenWidth * 0.14,
    borderRadius: screenWidth * 0.07,
  },
  radio: {
    width: 70,
    height: 36,
    borderWidth: 2,
    borderRadius: 18,
    borderColor: Colors.grey_fade,
    justifyContent: "center",
  },
  but: {
    height: 32,
    width: 32,
    borderRadius: 16,
  },
});

export default ZonesScreen;
